import { getAuthHeaders } from '../auth/auth.js';

const BASE_URL = 'https://api.figma.com/v1';

export async function getTeamProjects(teamId) {
  const res = await fetch(`${BASE_URL}/teams/${teamId}/projects`, {
    headers: getAuthHeaders(),
  });
  if (!res.ok) throw new Error(`Failed to fetch team projects: ${res.status}`);
  return res.json();
}

export async function getProjectFiles(projectId) {
  const res = await fetch(`${BASE_URL}/projects/${projectId}/files`, {
    headers: getAuthHeaders(),
  });
  if (!res.ok) throw new Error(`Failed to fetch project files: ${res.status}`);
  return res.json();
}

export async function getFile(fileKey) {
  const res = await fetch(`${BASE_URL}/files/${fileKey}`, {
    headers: getAuthHeaders(),
  });
  if (!res.ok) throw new Error(`Failed to fetch file data: ${res.status}`);
  return res.json();
}

import { getTeamProjects, getProjectFiles, getFile } from '../core/figma-api/figma-api.js';
import 'dotenv/config';

const TEAM_ID = process.env.TEST_TEAM_ID;
const PROJECT_ID = process.env.TEST_PROJECT_ID;
const FILE_KEY = process.env.TEST_FILE_KEY;

(async () => {
  try {
    if (TEAM_ID) {
      const projects = await getTeamProjects(TEAM_ID);
      console.log('✅ Team Projects:', projects);
    }

    if (PROJECT_ID) {
      const files = await getProjectFiles(PROJECT_ID);
      console.log('✅ Project Files:', files);
    }

    if (FILE_KEY) {
      const file = await getFile(FILE_KEY);
      console.log('✅ File Data:', file.name, '-', file.document.children?.length, 'pages');
    }
  } catch (err) {
    console.error('❌ API Error:', err);
  }
})();
