import fs from 'fs-extra';
import path from 'path';
import { resolveScope } from '../scope/resolveScope.js';

export async function runDiagnostic(scope, projectFolder) {
  const logDir = path.join(projectFolder, 'diagnostics');
  await fs.ensureDir(logDir);

  const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
  const filePath = path.join(logDir, `diag--${timestamp}.txt`);

  const lines = [];
  const summary = {
    success: true,
    issues: [],
    filePath
  };

  const token = process.env.FIGMA_PERSONAL_ACCESS_TOKEN;
  if (!token) {
    lines.push('âŒ FIGMA_PERSONAL_ACCESS_TOKEN is missing');
    summary.success = false;
    summary.issues.push('Missing FIGMA_PERSONAL_ACCESS_TOKEN');
  } else {
    lines.push('âœ… FIGMA_PERSONAL_ACCESS_TOKEN is present');
  }

  const result = await resolveScope(scope);

  if (!result.valid) {
    lines.push(`âŒ Scope resolution failed: ${result.error}`);
    summary.success = false;
    summary.issues.push(result.error);
  } else {
    lines.push(`âœ… File: ${result.fileName}`);
    if (scope.pageName) {
      lines.push(result.diagnostics.pageFound
        ? `âœ… Page "${scope.pageName}" found`
        : `âŒ Page "${scope.pageName}" not found`);
    }
    if (scope.nodeId) {
      if (result.diagnostics.nodeResolved) {
        lines.push(`âœ… Node ID resolved â†’ "${result.diagnostics.nodeName}" [${result.diagnostics.nodeType}]`);
      } else {
        lines.push(`âŒ Node ID "${scope.nodeId}" could not be resolved.`);
        summary.success = false;
        summary.issues.push(`Could not resolve node ID: ${scope.nodeId}`);
      }
    }
  }

  const content = [
    'ğŸ§ª Design Infra Diagnostic Log',
    '==============================',
    '',
    ...lines,
    '',
    'Logged at: ' + new Date().toLocaleString(),
  ].join('\n');

  await fs.writeFile(filePath, content);

  return summary;
}

import '../scripts/.env.loader.js';
import { runDiagnostic } from '../core/utils/diagnostics.js';
import path from 'path';

const fileKey = process.env.TEST_FILE_KEY;
const pageName = process.env.TEST_PAGE_NAME;
const nodeId = process.env.TEST_NODE_ID;

(async () => {
  console.log('\nğŸ§ª Running Diagnostic Check...');

  const projectFolder = path.resolve('.');
  const result = await runDiagnostic({ fileKey, pageName, nodeId }, projectFolder);

  if (result.success) {
    console.log('âœ… All checks passed.');
  } else {
    console.log('âš ï¸  Some checks failed. See log for details.');
  }

  console.log(`ğŸ“„ Log written to: ${result.filePath}\n`);
})();
