import { getFile } from '../../core/figma-api/figma-api.js';
import { getPagesInFile, getTopLevelFramesInPage } from '../../core/traversal/traversal.js';
import { analyzeFrameComponentUsage } from './analyze.js';
import 'dotenv/config';

const FILE_KEY = process.env.TEST_FILE_KEY;

(async () => {
  if (!FILE_KEY) {
    console.error('‚ùå TEST_FILE_KEY missing in .env');
    process.exit(1);
  }

  try {
    const file = await getFile(FILE_KEY);
    const pages = getPagesInFile(file);
    const componentsById = file.components || {};

    console.log(`‚úÖ Analyzing ${pages.length} page(s)...\n`);

    for (const page of pages) {
      const frames = getTopLevelFramesInPage(page);
      console.log(`üìÑ Page: ${page.name} ‚Üí ${frames.length} frame(s)`);

      for (const frame of frames) {
        const report = analyzeFrameComponentUsage(frame, componentsById);
        console.log(`  üñº Frame: ${frame.name}`);

        console.log(`    ‚úÖ Mint components: ${report.mintCount}`);
        console.log(`    üß© Local components: ${report.localCount}`);
        console.log(`    ‚õî Other component instances: ${report.otherCount}`);
      }
    }
  } catch (err) {
    console.error('‚ùå Error in plugin:', err);
  }
})();

import { classifyResolvedName } from './helpers.js';

export function analyzeFrameComponentUsage(frameNode, componentsById) {
  const counts = {
    mintCount: 0,
    localCount: 0,
    otherCount: 0,
  };

  function walk(node) {
    if (node.type === 'INSTANCE') {
      const resolvedName = componentsById?.[node.componentId]?.name || '';
      const category = classifyResolvedName(resolvedName);

      if (category === 'mint') counts.mintCount++;
      else if (category === 'local') counts.localCount++;
      else counts.otherCount++;
    }

    if (node.children && Array.isArray(node.children)) {
      node.children.forEach(walk);
    }
  }

  walk(frameNode);
  return counts;
}

export function classifyResolvedName(name) {
  const lower = name.toLowerCase();
  if (lower.includes('mint')) return 'mint';
  if (lower.includes('_local')) return 'local';
  return 'other';
}
